import React from 'react';
/* import '../../App.css'; */
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


class SignUp extends React.Component {
  constructor(props) {
    super(props)

    this.state = {

      name: '',
      contactno: '',
      password: '',
      confrimPassword: '',
      email: '',
    }
  }
  handlenameChange = (e) => {
    this.setState({
      name: e.target.value
    })
    console.log(this.state.name)
  }
  handleusernameChange = (e) => {
    this.setState({
      username: e.target.value
    })
  }
  handlecontactChange = (e) => {
    this.setState({
      contactno: e.target.value
    })
  }
  handlepasswordChange = (e) => {
    this.setState({
      password: e.target.value
    })
  }
  handleconfirmpassChange = (e) => {
    this.setState({
      confrimPassword: e.target.value
    })
  }
  handleemailChange = (e) => {
    this.setState({
      email: e.target.value
    })
  }


  handleSubmit = (event) => {
    toast.success('Submitted Successfully',{
      position:'top-center'
    })
   
    const data = {
      confirmpassword: this.state.confrimPassword,
      password: this.state.password,
      username: this.state.username,
      contactno: this.state.contactno,
      email: this.state.email,
      name: this.state.name
    }
    console.log("data", data)
    axios({
      method: 'post',
      url: "http://localhost:80800/user/adduser",
      data
    }).then((resp) => console.log("resp", resp))
      .catch(() => { })
    event.preventDefault()
  }


  render() {
    return (
      <>
        <div className='in'>
          <div className='container'>
            <h2 className='title'> Create Account</h2>
            <form className='form' onSubmit={this.handleSubmit}>
              <div className='input'>
                <label>name:  </label>
                <input type='text' value={this.state.name} onChange={this.handlenameChange} />
              </div>
              {/*  <div className='input'>
                    <label>Username:  </label>
                    <input type='text' onChange={this.handleusernameChange}/>
                </div> */}
              <div className='input'>
                <label>email:  </label>
                <input type='text' onChange={this.handleemailChange} />
              </div>
              <div classname='input'>
                <label>contactno: </label>
                <input type='text' onChange={this.handlecontactChange} />
              </div>
              <div className='input'>
                <label>password:  </label>
                <input type='text' onChange={this.handlepasswordChange} />
              </div>
              <div className='input'>
                <label> confirm password:  </label>
                <input type='password' onChange={this.handleconfirmpassChange} />
              </div>

              <div className='submit'>
                <button value="submit">submit</button>
              {/*   <ToastContainer /> */}


              </div>

            </form>
          </div>
        </div>
      </>
    )
  }
}
export default SignUp;




