import React, { useState } from 'react';
import '../../App.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';


const SignUp = () => {

  const [name, setName] = useState("")
  const [username, setUserName] = useState("")
  const [contact, setContact] = useState("")
  const [password, setPasword] = useState("")
  const [confrimPassword, setConfirm] = useState("")
  const [emailid, setEmail] = useState("")
  let navigate = useNavigate()
  const inputnameHandler = (e) => {
    setName(e.target.value)
  }
  const inputusernameHandler = (e) => {
    setUserName(e.target.value)
  }
  const inputemailidHandler = (e) => {
    setEmail(e.target.value)
  }
  const inputcontactHandler = (e) => {
    setContact(e.target.value)
  }
  const inputpasswordHandler = (e) => {
    setPasword(e.target.value)
  }
  const inputconfirmHandler = (e) => {
    setConfirm(e.target.value)
  }

  const handleSubmit = (e) => {
    toast.success('Submitted Successfully', {

      position: 'top-center'

    });
    const data = {
      confirmpassword: confrimPassword,
      password: password,
      username: username,
      contact: contact,
      emailId: emailid,
      name: name
    }
    axios.post('http://localhost:8081/carwash/adduser', data)
      .then(function (response) {
        console.log(response)
      })
      .catch(function (error) {
        console.log(error)
      })
    navigate('/login')
    e.preventDefault()
  }

  return (
    <>
      <div className='in'>
        <div className='container'>
          <h2 className='title'> Create Account</h2>
          <form className='form' onSubmit={handleSubmit}>
            <div className='input'>
              <label>name:  </label>
              <input type='text' value={name} onChange={inputnameHandler} />
            </div>
          
            <div className='in'>
              <label>email:  </label>
              <input type='text' value={emailid} onChange={inputemailidHandler} />
            </div>
            <div classname='in'>
              <label>contact: </label>
              <input type='text' value={contact} onChange={inputcontactHandler} />
            </div>
            <div className='in'>
              <label>password:  </label>
              <input type='text' value={password} onChange={inputpasswordHandler} />
            </div>
            <div className='in'>
              <label> confirm password:  </label>
              <input type='text' value={confrimPassword} onChange={inputconfirmHandler} />
            </div>

            <div >
              <button className='submit' value="submit" >submit</button>
            </div><br/>
            <p className='in'>Already had Account...?<a href='/login'>Login</a></p>
            <p className='in'>If your a washer.. <a href='/Washerlog'>Washer login</a></p>
            <div >
            </div>
          </form>
        </div>
      </div>
    </>
  )
}

export default SignUp;




